        {
            ImageString = ImageString.Substring(ImageString.IndexOf("base64") + 7);
        }
        byte[] bytes = Convert.FromBase64String(ImageString);
        Image Image;
        ms = new MemoryStream(bytes);
        Image = Image.FromStream(ms);
